#!/usr/bin/env node
import { runCli } from '@graphql-codegen/cli/cjs/cli.js';
import { cliError } from '@graphql-codegen/cli/cjs/utils/cli-error.js';
const [, , cmd] = process.argv;
runCli(cmd)
    .then(returnCode => {
    process.exit(returnCode);
})
    .catch(error => {
    cliError(error);
});
